using System;
using System.IO;
using System.Net;

static class Keys
{
 public const string ENVIRONMENT_URL = 'ENVIRONMENT_URL';
 public const string USERNAME = 'USERNAME';
 public const string API_KEY = 'API_KEY';
 public const string CLIENT_ID = 'CLIENT-ID';
}
public class Program
{
 public static void ProcessDocumentURL(string fileURL)
 {
 var httpWebRequest = (HttpWebRequest)WebRequest.Create('https://' + Keys.ENVIRONMENT_URL + '/api/v8/partner/documents/');
 httpWebRequest.ContentType = 'application/json';
 httpWebRequest.Method = 'POST';
 httpWebRequest.Headers.Add('Authorization', 'apikey ' + Keys.USERNAME + ':' + Keys.API_KEY);
 httpWebRequest.Headers.Add('Client-id', Keys.CLIENT_ID);
 using (var streamWriter = new StreamWriter(httpWebRequest.GetRequestStream()))
 {
 string json = '{"file_name":"receipt.jpg",' +
 '"file_url":"'+ fileURL +'"}';
 streamWriter.Write(json);
 }
 var httpResponse = (HttpWebResponse)httpWebRequest.GetResponse();
 using (var streamReader = new StreamReader(httpResponse.GetResponseStream()))
 {
 var jsonResponse = streamReader.ReadToEnd();
 Console.WriteLine(String.Format('Response: {0}', jsonResponse));
 }
 }
 public static void Main(string[] args)
 {
 var fileURL = 'https://cdn.veryfi.com/receipts/ceb916d3-2ebe-4277-9d47-bc5f1499fcb0.png";
 ProcessDocumentURL(fileURL);
 }
 }
